import React, { Component,useContext,useState } from 'react';
import Container from '@material-ui/core/Container';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import AddCircleIcon from '@material-ui/icons/AddCircle';
import {useLocation} from 'react-router-dom'
import { EventContext } from './EventContext';

const AddPost = () => {
    const location = useLocation()
    const [post,setPost] = useState('')

    const add = (e) => {
        const name = e.target.name
        setPost({...post, name : e.target.name})
    }

    

    return ( 

        
        <Container maxWidth="sm" style={{textAlign: "center", marginTop: "25px"}}>
             <TextField id="standard-basic" label="Name" name='name'  onChange={(e)=>add(e)}/><br/><br/>
             <TextField multiline='true' id="standard-basic" name='content' label="content"  onChange={(e)=>add(e)}/><br/><br/>
             <TextField id="standard-basic" label="Picture URL"  name='picture' onChange={(e)=>add(e)}/><br/><br/>
             <Button  onClick={ console.log('yes')}   variant="contained">Add Event</Button>



        </Container>


    );
}
 
export default AddPost;
